import React from 'react';
import axios from 'axios';

const AuthorItem = ({author}) => {
	return(
		<tr>
			<td>
			{author.username}
		</td>
			<td>
			{author.first_name}
		</td>
			<td>
			{author.last_name}
		</td>
			<td>
			{author.email}
		</td>
		</tr>
	)
}

class AuthorsList extends React.Component {
   constructor(props) {
       super(props)
       this.state = {
           'authors': [],
       }
   }

	componentDidMount() {
		axios.get('http://127.0.0.1:8000/api/authors')
       .then(response => {
           const authors = response.data
               this.setState(
               {
                   'authors': authors
               }
           )
       }).catch(error => console.log(error))
	}
   render () {
	   return(
			   <table>
			   <tr>
			   <td>
			   username
		   </td>

			   <td>
			   first_name
		   </td>

			   <td>
			   last_name
		   </td>

		       <td>
			   email
		   </td>
			   </tr>
			   {this.authors.map((author) => <AuthorItem author= {author} />)}
		   </table>
	   )
	}
}

export default AuthorsList;
